pragma solidity 0.8.17;

/* solhint-disable no-empty-blocks */

import {IAuthorizationModule} from "../interfaces/IAuthorizationModule.sol";
import {ISignatureValidator} from "../interfaces/ISignatureValidator.sol";

contract AuthorizationModulesConstants {
    uint256 internal constant VALIDATION_SUCCESS = 0;
    uint256 internal constant SIG_VALIDATION_FAILED = 1;
}

abstract contract BaseAuthorizationModule is
    IAuthorizationModule,
    ISignatureValidator,
    AuthorizationModulesConstants
{}

contracts/modules/KeyManagementContractR.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

// interface for modules to verify singatures signed over userOpHash
interface IKeyManagementAuthorizationModule {
    function validate(bytes calldata data) external view returns (bool result);
}

// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "./interfaces/IKeyManagementAuthorizationModule.sol";

import {SignatureRSV, EthereumUtils} from "@oasisprotocol/sapphire-contracts/contracts/EthereumUtils.sol";
import {UserOperation} from "@account-abstraction/contracts/interfaces/UserOperation.sol";
import {Sapphire} from "@oasisprotocol/sapphire-contracts/contracts/Sapphire.sol";
import {BaseAuthorizationModule} from "../BaseAuthorizationModule.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract KeyManagement is BaseAuthorizationModule {
    using ECDSA for bytes32;

    address internal constant SENTINEL_MODULES = address(0x1);

    mapping(address => address) internal _modules;
    mapping(address => address) internal publicAddress;
    mapping(address => bytes32) private privateSecret;

    bool private _initialized;

    error WrongValidationModule(address validationModule);

    constructor() {}

    function initForSmartAccount(address _owner) external returns (address) {
        require(!_initialized, "AlreadyInitialized");
        address keypairAddress;
        bytes32 keypairSecret;
        (keypairAddress, keypairSecret) = EthereumUtils.generateKeypair();
        publicAddress[_owner] = keypairAddress;
        privateSecret[_owner] = keypairSecret;
        return address(this);
    }

    modifier onlyOwner(bytes calldata data) {
        (, address validationModule) = abi.decode(data, (bytes, address));
        if (_modules[validationModule] != address(0)) {
            require(
                IKeyManagementAuthorizationModule(validationModule).validate(
                    data
                ) == true,
                "Validate wrong"
            );
        } else {
            revert WrongValidationModule(validationModule);
        }
        _;
    }

    function sign(
        bytes calldata authenticationData,
        address smartAccount,
        bytes32 digest
    )
        public
        view
        returns (
            // onlyOwner(authenticationData, smartAccount)
            SignatureRSV memory
        )
    {
        return
            EthereumUtils.sign(
                publicAddress[smartAccount],
                privateSecret[smartAccount],
                digest
            );
    }

    function validateUserOp(
        UserOperation calldata userOp,
        bytes32 userOpHash
    ) external view virtual returns (uint256) {
        (bytes memory cleanEcdsaSignature, ) = abi.decode(
            userOp.signature,
            (bytes, address)
        );
        if (_verifySignature(userOpHash, cleanEcdsaSignature, userOp.sender)) {
            return VALIDATION_SUCCESS;
        }
        return SIG_VALIDATION_FAILED;
    }

    function isValidSignature(
        bytes32 dataHash,
        bytes memory moduleSignature
    ) public view override returns (bytes4) {
        return
            isValidSignatureForAddress(dataHash, moduleSignature, msg.sender);
    }

    function isValidSignatureForAddress(
        bytes32 dataHash,
        bytes memory moduleSignature,
        address smartAccount
    ) public view virtual returns (bytes4) {
        if (_verifySignature(dataHash, moduleSignature, smartAccount)) {
            return EIP1271_MAGIC_VALUE;
        }
        return bytes4(0xffffffff);
    }

    function _verifySignature(
        bytes32 dataHash,
        bytes memory signature,
        address smartAccount
    ) internal view returns (bool) {
        address keypairAddress = publicAddress[smartAccount];
        address recovered = (dataHash.toEthSignedMessageHash()).recover(
            signature
        );
        if (keypairAddress == recovered) {
            return true;
        }
        recovered = dataHash.recover(signature);
        if (keypairAddress == recovered) {
            return true;
        }
        return false;
    }
}

// bool verify = Sapphire.verify(
//     Sapphire.SigningAlg.Secp256k1PrehashedKeccak256,
//     bytes(keypairAddress),
//     dataHash,
//     "",
//     signature
// );
// return verify;

// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract EcdsaOwnershipRegistryModule {
    using ECDSA for bytes32;

    string public constant NAME = "ECDSA Ownership Module For Key Management";
    mapping(address => address) internal _keyManagementOwners;
    uint256 limitBlock = 10;

    error AlreadyInited(address smartAccount);
    error ZeroAddressNotAllowedAsOwner();
    error NoOwnerRegisteredForSmartAccount(address smartAccount);
    error WrongSignatureLength();

    function initForSmartAccount(address eoaOwner) external returns (address) {
        if (_keyManagementOwners[msg.sender] != address(0))
            revert AlreadyInited(msg.sender);
        if (eoaOwner == address(0)) revert ZeroAddressNotAllowedAsOwner();
        _keyManagementOwners[msg.sender] = eoaOwner;
        return address(this);
    }

    function validate(
        bytes calldata data
    ) external view virtual returns (bool) {
        (bytes32 plaintext, uint256 numBlock, bytes memory signature) = abi
            .decode(data, (bytes32, uint256, bytes));

        require(block.number - numBlock < limitBlock, "too late");

        bytes32 ciphertext = keccak256(
            abi.encode(plaintext, blockhash(numBlock), numBlock)
        );

        if (_verifySignature(ciphertext, signature, msg.sender)) {
            return true;
        }
        return false;
    }

    function _verifySignature(
        bytes32 dataHash,
        bytes memory signature,
        address smartAccount
    ) internal view returns (bool) {
        address expectedSigner = _keyManagementOwners[smartAccount];
        if (expectedSigner == address(0))
            revert NoOwnerRegisteredForSmartAccount(smartAccount);
        if (signature.length < 65) revert WrongSignatureLength();
        address recovered = (dataHash.toEthSignedMessageHash()).recover(
            signature
        );
        if (expectedSigner == recovered) {
            return true;
        }
        recovered = dataHash.recover(signature);
        if (expectedSigner == recovered) {
            return true;
        }
        return false;
    }

    /**
     * @dev Checks if the address provided is a smart contract.
     * @param account Address to be checked.
     */
    function _isSmartContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }
}
