// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {UserOperation} from "@account-abstraction/contracts/interfaces/UserOperation.sol";

struct PaymasterData {
    address paymasterId;
    uint48 validUntil;
    uint48 validAfter;
    bytes signature;
    uint256 signatureLength;
}

struct PaymasterContext {
    address paymasterId;
    // could add maxFeePerGas and maxPriorityFeePerGas if needed
    // by making approprate changes in paymaster contract
}

/**
 * @title PaymasterHelpers - helper functions for paymasters
 */
library PaymasterHelpers {
    using ECDSA for bytes32;

    /**
     * @dev Encodes the paymaster context: paymasterId and gasPrice
     * @param data PaymasterData passed
     */
    function paymasterContext(
        PaymasterData memory data
    )
        internal
        pure
        returns (
            // Could add maxFeePerGas and maxPriorityFeePerGas if needed
            bytes memory context
        )
    {
        return abi.encode(data.paymasterId);
    }

    /**
     * @dev Decodes paymaster data assuming it follows PaymasterData
     */
    function decodePaymasterData(
        UserOperation calldata op
    ) internal pure returns (PaymasterData memory) {
        bytes calldata paymasterAndData = op.paymasterAndData;
        (
            address paymasterId,
            uint48 validUntil,
            uint48 validAfter,
            bytes memory signature
        ) = abi.decode(paymasterAndData[20:], (address, uint48, uint48, bytes));
        return
            PaymasterData(
                paymasterId,
                validUntil,
                validAfter,
                signature,
                signature.length
            );
    }

    /**
     * @dev Decodes paymaster context assuming it follows PaymasterContext
     */
    function decodePaymasterContext(
        bytes memory context
    ) internal pure returns (PaymasterContext memory) {
        address paymasterId = abi.decode(context, (address));
        return PaymasterContext(paymasterId);
    }
}

contracts/paymasters/TokenPaymaster.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.17;

// Import the required libraries and contracts
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "@account-abstraction/contracts/core/BasePaymaster.sol";
import "./utils/LuminexSwapHelper.sol";

/**
 * @title TokenPaymaster
 * @notice This Paymaster covers gas fees in exchange for ERC20 tokens charged using allowance pre-issued by ERC-4337 accounts.
 * The contract refunds excess tokens if the actual gas cost is lower than the initially provided amount.
 * The token price cannot be queried in the validation code due to storage access restrictions of ERC-4337.
 * It is theoretically possible the token has depreciated so much since the last 'postOp' the refund becomes negative.
 * The contract reverts the inner user transaction in that case but keeps the charge.
 * The contract also allows honest clients to prepay tokens at a higher price to avoid getting reverted.
 * It also allows updating price configuration and withdrawing tokens by the contract owner.
 * @dev Inherits from BasePaymaster.
 */
contract TokenPaymaster is BasePaymaster, LuminexSwapHelper {
    struct TokenPaymasterConfig {
        /// @notice Estimated gas cost for refunding tokens after the transaction is completed
        uint48 refundPostopCost;
        /// @notice Expected minimum amount of native tokens to be exchanged in a ERC20 to native token swap when refill EntryPoint deposit
        uint256 minSwapAmount;
    }

    // List of supported ERC20 tokens to be used for charging gas fees
    mapping(address => bool) public tokenSupport;
    mapping(address => uint256) public tokenToOrdinal;
    mapping(uint256 => address) public ordinalToToken;

    uint256 public countTokenSupport;
    uint256 public totalToken;

    address[] public listTokenSupport;

    event ConfigUpdated(TokenPaymasterConfig tokenPaymasterConfig);

    event UserOperationSponsored(
        address indexed user,
        uint256 actualTokenCharge,
        uint256 actualGasCost
    );

    event Received(address indexed sender, uint256 value);

    TokenPaymasterConfig public tokenPaymasterConfig;

    /// @notice Initializes the TokenPaymaster contract with the given parameters.
    /// @param _entryPoint The EntryPoint contract used in the Account Abstraction infrastructure.
    /// @param _wrappedNative The ERC-20 token that wraps the native asset for current chain.
    /// @param _luminexRouterV1 The LuminexRouterV1 contract used for token swaps.
    /// @param _tokenPaymasterConfig The configuration for the Token Paymaster.
    /// @param _owner The address that will be set as the owner of the contract.
    constructor(
        IEntryPoint _entryPoint,
        IERC20 _wrappedNative,
        ILuminexRouterV1 _luminexRouterV1,
        TokenPaymasterConfig memory _tokenPaymasterConfig,
        address _owner
    )
        BasePaymaster(_entryPoint)
        LuminexSwapHelper(address(_luminexRouterV1), address(_wrappedNative))
    {
        setTokenPaymasterConfig(_tokenPaymasterConfig);
        transferOwnership(_owner);
    }

    /// @notice Adds support for an ERC20 token to be used for charging gas fees.
    /// @param token The address of the ERC20 token to be supported.
    function addERC20Support(address token) public onlyOwner {
        if (tokenSupport[token]) revert("token was enabled");

        if (tokenToOrdinal[token] == 0) {
            // add new token
            totalToken++;
            countTokenSupport++;
            tokenToOrdinal[token] = totalToken;
            ordinalToToken[totalToken] = token;
            tokenSupport[token] = true;
        } else {
            // change state
            countTokenSupport++;
            tokenSupport[token] = true;
        }
    }

    function removeERC20Support(address token) public onlyOwner {
        require(tokenSupport[token], "Token was removed");
        countTokenSupport--;
        tokenSupport[token] = false;
    }

    function getListTokenSupport()
        public
        view
        returns (address[] memory result)
    {
        result = new address[](countTokenSupport);
        uint256 j = 0;
        for (uint256 i = 1; i <= totalToken; i++) {
            if (tokenSupport[ordinalToToken[i]] == true) {
                result[j] = ordinalToToken[i];
                j++;
            }
        }
        return result;
    }

    /// @notice Updates the configuration for the Token Paymaster.
    /// @param _tokenPaymasterConfig The new configuration struct.
    function setTokenPaymasterConfig(
        TokenPaymasterConfig memory _tokenPaymasterConfig
    ) public onlyOwner {
        tokenPaymasterConfig = _tokenPaymasterConfig;
        emit ConfigUpdated(_tokenPaymasterConfig);
    }

    /// @notice Allows the contract owner to withdraw a specified amount of tokens from the contract.
    /// @param token The ERC20 token to withdraw.
    /// @param to The address to transfer the tokens to.
    /// @param amount The amount of tokens to transfer.
    function withdrawToken(
        IERC20 token,
        address to,
        uint256 amount
    ) external onlyOwner {
        SafeERC20.safeTransfer(token, to, amount);
    }

    /// @notice Validates a paymaster user operation and calculates the required token amount for the transaction.
    /// @param userOp The user operation data.
    /// @param requiredPreFund The maximum cost (in native token) the paymaster has to prefund.
    /// @return context The context containing the token amount and user sender address (if applicable).
    /// @return validationResult A uint256 value indicating the result of the validation (always 0 in this implementation).
    function _validatePaymasterUserOp(
        UserOperation calldata userOp,
        bytes32,
        uint256 requiredPreFund
    )
        internal
        override
        returns (bytes memory context, uint256 validationResult)
    {
        unchecked {
            uint256 maxFeePerGas = userOp.maxFeePerGas;
            uint256 refundPostopCost = tokenPaymasterConfig.refundPostopCost;
            uint256 postOpGasLimit = uint128(
                bytes16(userOp.paymasterAndData[36:52])
            );
            require(
                refundPostopCost < postOpGasLimit,
                "TPM: postOpGasLimit too low"
            );

            uint256 preChargeNative = requiredPreFund +
                (refundPostopCost * maxFeePerGas);
            address token = address(bytes20(userOp.paymasterAndData[52:72]));
            uint256 tokenAmount = 0;
            if (token == address(wrappedNative)) {
                tokenAmount = preChargeNative;
            } else {
                require(tokenSupport[token], "Token not support");
                tokenAmount = estimateNativeToToken(token, preChargeNative);
            }

            SafeERC20.safeTransferFrom(
                IERC20(token),
                userOp.sender,
                address(this),
                tokenAmount
            );

            context = abi.encode(
                tokenAmount,
                token,
                userOp.sender,
                userOp.maxFeePerGas
            );

            validationResult = 0;
        }
    }

    /// @notice Performs post-operation tasks, such as updating the token price and refunding excess tokens.
    /// @dev This function is called after a user operation has been executed or reverted.
    /// @param context The context containing the token amount and user sender address.
    /// @param actualGasCost The actual gas cost of the transaction.
    //      It is not the same as tx.gasprice, which is what the bundler pays.

    function _postOp(
        PostOpMode,
        bytes calldata context,
        uint256 actualGasCost
    ) internal override {
        unchecked {
            (
                uint256 preCharge,
                address token,
                address userOpSender,
                uint256 actualUserOpFeePerGas
            ) = abi.decode(context, (uint256, address, address, uint256));

            // Refund tokens based on actual gas cost
            uint256 actualChargeNative = actualGasCost +
                tokenPaymasterConfig.refundPostopCost *
                actualUserOpFeePerGas;

            uint256 actualTokenNeeded = 0;

            // if the token is wrapped native token, the actual token needed is the actual native token charged
            if (token == address(wrappedNative)) {
                actualTokenNeeded = actualChargeNative;
                // otherwise, estimate the token needed using router helper
            } else {
                actualTokenNeeded = estimateNativeToToken(
                    token,
                    actualChargeNative
                );
            }

            if (preCharge > actualTokenNeeded) {
                // If the initially provided token amount is greater than the actual amount needed, refund the difference
                SafeERC20.safeTransfer(
                    IERC20(token),
                    userOpSender,
                    preCharge - actualTokenNeeded
                );
            } else if (preCharge < actualTokenNeeded) {
                // Attempt to cover Paymaster's gas expenses by withdrawing the 'overdraft' from the client
                // If the transfer reverts also revert the 'postOp' to remove the incentive to cheat
                SafeERC20.safeTransferFrom(
                    IERC20(token),
                    userOpSender,
                    address(this),
                    actualTokenNeeded - preCharge
                );
            }

            emit UserOperationSponsored(
                userOpSender,
                actualTokenNeeded,
                actualGasCost
            );

            refillEntryPointDeposit(token);
        }
    }

    /// @notice If necessary this function uses this Paymaster's token balance to refill the deposit on EntryPoint
    /// @param token The ERC20 token address to be used for refilling the deposit
    function refillEntryPointDeposit(address token) private {
        // if the ERC20 token is wrapped native token and paymaster's balance of that token is greater than minSwapAmount, unwrap and deposit to entry point
        if (address(token) == address(wrappedNative)) {
            if (
                IERC20(token).balanceOf(address(this)) >
                tokenPaymasterConfig.minSwapAmount
            ) {
                unwrapWeth(IERC20(token).balanceOf(address(this)));
                entryPoint.depositTo{value: address(this).balance}(
                    address(this)
                );
            }
        } else {
            // if the ERC20 token is not wrapped native token and paymaster's balance of that token is greater than minSwapAmount, swap and deposit to entry point
            uint256 tokenBalance = IERC20(token).balanceOf(address(this));

            uint256 estimateReceiveNative = estimateTokenToNative(
                token,
                tokenBalance
            );

            if (estimateReceiveNative > tokenPaymasterConfig.minSwapAmount) {
                _swapTokenToNative(token, tokenBalance);
                entryPoint.depositTo{value: address(this).balance}(
                    address(this)
                );
            }
        }
    }

    receive() external payable {
        emit Received(msg.sender, msg.value);
    }

    /// @notice For contract owner to withdraw ETH from the contract
    /// @param recipient The address to transfer the ETH to
    /// @param amount The amount of ETH to transfer
    function withdrawEth(
        address payable recipient,
        uint256 amount
    ) external onlyOwner {
        (bool success, ) = recipient.call{value: amount}("");
        require(success, "withdraw failed");
    }
}

contracts/paymasters/utils/LuminexSwapHe.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.17;
import "../interfaces/ILuminexRouterV1.sol";
import "../interfaces/IWrappedNative.sol";

/**
 * @title LuminexSwapHelper
 * @notice Helper contract to interact with LuminexRouterV1 for swapping native token to ERC20 token and vice versa
 */
abstract contract LuminexSwapHelper {
    address public immutable luminexRouterV1;
    address public immutable wrappedNative;

    /// @param _luminexRouterV1 LuminexRouterV1 contract address
    /// @param _wrappnative Wrapped native token contract address
    constructor(address _luminexRouterV1, address _wrappnative) {
        luminexRouterV1 = _luminexRouterV1;
        wrappedNative = _wrappnative;
    }

    /// @notice estimate ERC20 token receive in exchange with amountIn of native token by calling LuminexRouterV1
    /// @param token ERC20 token address
    /// @param amountIn amount of native token
    /// @return amountOut amount of ERC20 token receive in exchange with amountIn of native token
    function estimateNativeToToken(
        address token,
        uint256 amountIn
    ) public view returns (uint256 amountOut) {
        address[] memory path = new address[](2);
        path[0] = wrappedNative;
        path[1] = token;
        uint256[] memory result = ILuminexRouterV1(luminexRouterV1)
            .getAmountsOut(amountIn, path);
        amountOut = result[1];
    }

    /// @notice estimate native token receive in exchange with amountIn of ERC20 token by calling LuminexRouterV1
    /// @param token ERC20 token address
    /// @param amountIn amount of ERC20 token
    /// @return amountOut amount of native token receive in exchange with amountIn of ERC20 token
    function estimateTokenToNative(
        address token,
        uint256 amountIn
    ) public view returns (uint256 amountOut) {
        address[] memory path = new address[](2);
        path[0] = token;
        path[1] = wrappedNative;
        uint256[] memory result = ILuminexRouterV1(luminexRouterV1)
            .getAmountsOut(amountIn, path);
        amountOut = result[1];
    }

    /// @notice swap native token to ERC20 token by calling LuminexRouterV1
    /// @param token ERC20 token address
    /// @param amountIn amount of native token
    function _swapTokenToNative(address token, uint256 amountIn) internal {
        address[] memory path = new address[](2);
        path[0] = token;
        path[1] = wrappedNative;
        ILuminexRouterV1(luminexRouterV1).swapExactTokensForROSE(
            amountIn,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    /// @notice unwrap WETH by withdrawing
    /// @param amount amount of WETH to withdraw
    function unwrapWeth(uint256 amount) internal {
        IWETH(address(wrappedNative)).withdraw(amount);
    }
}

contracts/paymasters/verifying/singleton.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {UserOperation, UserOperationLib} from "@account-abstraction/contracts/interfaces/UserOperation.sol";
import "../../BasePaymaster.sol";
import {PaymasterHelpers, PaymasterData, PaymasterContext} from "../../PaymasterHelpers.sol";
import {SingletonPaymasterErrors} from "../../../common/Errors.sol";

/**
 * @title A sample paymaster that uses external service to decide whether to pay for the UserOp.
 * @dev The paymaster trusts an external signer to sign the transaction.
 * The calling user must pass the UserOp to that external signer first, which performs whatever
 * off-chain verification before signing the UserOp.
 * @notice That this signature is NOT a replacement for wallet signature:
 *  - The paymaster signs to agree to PAY for GAS.
 *  - The wallet signs to prove identity and wallet ownership.
 */
contract VerifyingSingletonPaymaster is
    BasePaymaster,
    ReentrancyGuard,
    SingletonPaymasterErrors
{
    using ECDSA for bytes32;
    using UserOperationLib for UserOperation;
    using PaymasterHelpers for UserOperation;
    using PaymasterHelpers for bytes;
    using PaymasterHelpers for PaymasterData;

    // Gas used in EntryPoint._handlePostOp() method (including this#postOp() call)
    uint256 private _unaccountedEPGasOverhead;
    mapping(address => uint256) public paymasterIdBalances;

    address public verifyingSigner;

    event EPGasOverheadChanged(
        uint256 indexed _oldValue,
        uint256 indexed _newValue
    );

    event VerifyingSignerChanged(
        address indexed _oldSigner,
        address indexed _newSigner,
        address indexed _actor
    );
    event GasDeposited(address indexed _paymasterId, uint256 indexed _value);
    event GasWithdrawn(
        address indexed _paymasterId,
        address indexed _to,
        uint256 indexed _value
    );
    event GasBalanceDeducted(
        address indexed _paymasterId,
        uint256 indexed _charge
    );

    constructor(
        address _owner,
        IEntryPoint _entryPoint,
        address _verifyingSigner
    ) payable BasePaymaster(_owner, _entryPoint) {
        if (address(_entryPoint) == address(0)) revert EntryPointCannotBeZero();
        if (_verifyingSigner == address(0))
            revert VerifyingSignerCannotBeZero();
        assembly {
            sstore(verifyingSigner.slot, _verifyingSigner)
        }
        _unaccountedEPGasOverhead = 9600;
    }

    /**
     * @dev Deposit funds for a given paymasterId to cover transaction fees.
     * @param paymasterId Identifier of the dapp receiving the deposit.
     */
    function depositFor(address paymasterId) external payable nonReentrant {
        if (paymasterId == address(0)) revert PaymasterIdCannotBeZero();
        if (msg.value == 0) revert DepositCanNotBeZero();
        paymasterIdBalances[paymasterId] =
            paymasterIdBalances[paymasterId] +
            msg.value;
        ENTRY_POINT.depositTo{value: msg.value}(address(this));
        emit GasDeposited(paymasterId, msg.value);
    }

    /**
     * @dev Set a new verifying signer address.
     * Can only be called by the owner of the contract.
     * @param _newVerifyingSigner The new address to be set as the verifying signer.
     * @notice If _newVerifyingSigner is set to zero address, it will revert with an error.
     * After setting the new signer address, it will emit an event VerifyingSignerChanged.
     */
    function setSigner(address _newVerifyingSigner) external payable onlyOwner {
        if (_newVerifyingSigner == address(0))
            revert VerifyingSignerCannotBeZero();
        address oldSigner = verifyingSigner;
        assembly {
            sstore(verifyingSigner.slot, _newVerifyingSigner)
        }
        emit VerifyingSignerChanged(oldSigner, _newVerifyingSigner, msg.sender);
    }

    function setUnaccountedEPGasOverhead(uint256 value) external onlyOwner {
        uint256 oldValue = _unaccountedEPGasOverhead;
        _unaccountedEPGasOverhead = value;
        emit EPGasOverheadChanged(oldValue, value);
    }

    /**
     * @dev get the current deposit for paymasterId (Dapp Depositor address)
     * @param paymasterId dapp identifier
     */
    function getBalance(
        address paymasterId
    ) external view returns (uint256 balance) {
        balance = paymasterIdBalances[paymasterId];
    }

    /**
     @dev Override the default implementation.
     */
    function deposit() public payable virtual override {
        revert("user DepositFor instead");
    }

    /**
     * @dev Withdraws specified gas tokens from paymaster's balance to a given address.
     * @param withdrawAddress Address receiving the gas tokens.
     * @param amount Amount of gas tokens to withdraw.
     */
    function withdrawTo(
        address payable withdrawAddress,
        uint256 amount
    ) public override nonReentrant {
        if (withdrawAddress == address(0)) revert CanNotWithdrawToZeroAddress();
        uint256 currentBalance = paymasterIdBalances[msg.sender];
        if (amount > currentBalance)
            revert InsufficientBalance(amount, currentBalance);
        paymasterIdBalances[msg.sender] =
            paymasterIdBalances[msg.sender] -
            amount;
        ENTRY_POINT.withdrawTo(withdrawAddress, amount);
        emit GasWithdrawn(msg.sender, withdrawAddress, amount);
    }

    /**
     * @dev Called by off-chain service for signing, and on-chain in validatePaymasterUserOp for validation.
     * @notice Signature covers all UserOperation fields except "paymasterAndData" which carries the signature.
     * @return Hash to sign off-chain and validate on-chain.
     */
    function getHash(
        UserOperation calldata userOp,
        address paymasterId,
        uint48 validUntil,
        uint48 validAfter
    ) public view returns (bytes32) {
        // can't use userOp.hash(), since it contains also the paymasterAndData itself.
        address sender = userOp.getSender();
        return
            keccak256(
                abi.encode(
                    sender,
                    userOp.nonce,
                    keccak256(userOp.initCode),
                    keccak256(userOp.callData),
                    userOp.callGasLimit,
                    userOp.verificationGasLimit,
                    userOp.preVerificationGas,
                    userOp.maxFeePerGas,
                    userOp.maxPriorityFeePerGas,
                    block.chainid,
                    address(this),
                    paymasterId,
                    validUntil,
                    validAfter
                )
            );
    }

    /**
     * @dev Executes the paymaster's payment conditions
     * @param mode tells whether the op succeeded, reverted, or if the op succeeded but cause the postOp to revert
     * @param context payment conditions signed by the paymaster in `validatePaymasterUserOp`
     * @param actualGasCost amount to be paid to the entry point in wei
     */
    function _postOp(
        PostOpMode mode,
        bytes calldata context,
        uint256 actualGasCost
    ) internal virtual override {
        (mode);
        PaymasterContext memory data = context.decodePaymasterContext();
        address extractedPaymasterId = data.paymasterId;
        uint256 balToDeduct = actualGasCost +
            _unaccountedEPGasOverhead *
            tx.gasprice;
        paymasterIdBalances[extractedPaymasterId] =
            paymasterIdBalances[extractedPaymasterId] -
            balToDeduct;
        emit GasBalanceDeducted(extractedPaymasterId, balToDeduct);
    }

    /**
     * @dev Verify that an external signer signed the paymaster data of a user operation.
     * The paymaster data is expected to be the paymaster and a signature over the entire request parameters.
     * @param userOp The UserOperation struct that represents the current user operation.
     * userOpHash The hash of the UserOperation struct.
     * @param requiredPreFund The required amount of pre-funding for the paymaster.
     * @return context A context string returned by the entry point after successful validation.
     * @return validationData An integer returned by the entry point after successful validation.
     */
    function _validatePaymasterUserOp(
        UserOperation calldata userOp,
        bytes32 /*userOpHash*/,
        uint256 requiredPreFund
    )
        internal
        view
        override
        returns (bytes memory context, uint256 validationData)
    {
        PaymasterData memory paymasterData = userOp.decodePaymasterData();
        bytes32 hash = getHash(
            userOp,
            paymasterData.paymasterId,
            paymasterData.validUntil,
            paymasterData.validAfter
        );
        uint256 sigLength = paymasterData.signatureLength;
        // Ensure revert reason is from "VerifyingPaymaster" not "ECDSA" on invalid signature.

        if (sigLength != 65) revert InvalidPaymasterSignatureLength(sigLength);
        // Don't revert on signature failure: return SIG_VALIDATION_FAILED.
        if (
            verifyingSigner !=
            hash.toEthSignedMessageHash().recover(paymasterData.signature)
        ) {
            // Empty context and sigFailed with time range provided
            return (
                "",
                _packValidationData(
                    true,
                    paymasterData.validUntil,
                    paymasterData.validAfter
                )
            );
        }
        if (requiredPreFund > paymasterIdBalances[paymasterData.paymasterId])
            revert InsufficientBalance(
                requiredPreFund,
                paymasterIdBalances[paymasterData.paymasterId]
            );
        return (
            PaymasterHelpers.paymasterContext(paymasterData),
            _packValidationData(
                false,
                paymasterData.validUntil,
                paymasterData.validAfter
            )
        );
    }
}

