pragma solidity ^0.8.17;
import "solidity-bytes-utils/contracts/BytesLib.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import {UserOperation} from "@account-abstraction/contracts/interfaces/UserOperation.sol";

library SellValidator {
    using BytesLib for bytes;

    struct ExactOutputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountOut;
        uint256 amountInMaximum;
        uint160 sqrtPriceLimitX96;
    }

    bytes4 public constant ERC20_APPROVE_SELECTOR = 0x095ea7b3;
    bytes4 public constant EXACT_OUTPUT_SINGLE_SELECTOR = 0xdb3e2198;
    bytes4 public constant UNIV2_SWAP_TOKENS_FOR_EXACT_ETH = 0x4a25d94a;

    // execute_ncC(address,uint256,bytes)
    bytes4 public constant EXECUTE_OPTIMIZED_SELECTOR = 0x0000189a;
    // function executeBatch_y6U( address[] calldata dest, uint256[] calldata value, bytes[] calldata func)
    bytes4 public constant EXECUTE_BATCH_SELECTOR = 0x00004680;

    /**
     * @dev validates if the _op (UserOperation) matches the SessionKey permissions
     * and that _op has been signed by this SessionKey
     * Please mind the decimals of your exact token when setting maxAmount
     * @param token the non-native token of the pair
     * @return true if the _op is valid, false otherwise.
     */
    function validateSell(
        UserOperation memory op,
        address token,
        uint256 spentAmount,
        uint256 payment,
        address WRAPPED_NATIVE_TOKEN,
        address ROUTER,
        address FEE_RECIPIENT
    ) internal pure returns (bool) {
        bytes4 opFncSig = bytes4(op.callData.slice(0, 4));

        bytes memory opData = op.callData.slice(4, op.callData.length - 4);

        if (opFncSig == EXECUTE_OPTIMIZED_SELECTOR) {
            require(payment == 0, "SV: no payment required");
            (address router, , bytes memory data) = abi.decode(
                opData,
                (address, uint256, bytes)
            );
            require(router == ROUTER, "SV: invalid router");
            validateSellTx(
                data,
                op.sender,
                token,
                WRAPPED_NATIVE_TOKEN,
                spentAmount
            );
        } else if (opFncSig == EXECUTE_BATCH_SELECTOR) {
            (
                address[] memory addresses,
                uint256[] memory callValues,
                bytes[] memory data
            ) = abi.decode(opData, (address[], uint256[], bytes[]));

            require(
                addresses.length == callValues.length &&
                    addresses.length == data.length,
                "SV: invalid tx list's length"
            );
            uint256 fncNum = addresses.length;
            require(fncNum >= 2 && fncNum <= 3, "SV: invalid number of txs");

            uint256 sellTxIndex;
            if (payment > 0) {
                require(
                    addresses[fncNum - 1] == FEE_RECIPIENT,
                    "SV: must pay fee"
                );
                require(
                    callValues[fncNum - 1] == payment,
                    "SV: invalid payment"
                );
                sellTxIndex = fncNum - 2;
            } else {
                sellTxIndex = fncNum - 1;
            }

            require(addresses[sellTxIndex] == ROUTER, "SV: invalid router");
            validateSellTx(
                data[sellTxIndex],
                op.sender,
                token,
                WRAPPED_NATIVE_TOKEN,
                spentAmount
            );

            if (sellTxIndex == 1) {
                require(addresses[0] == token, "SV: must approve token");

                bytes4 fncSig = bytes4(data[0].slice(0, 4));
                require(
                    fncSig == ERC20_APPROVE_SELECTOR,
                    "SV: Invalid op funtion signature"
                );

                bytes memory funcData = data[0].slice(4, data[0].length - 4);
                (address router, ) = abi.decode(funcData, (address, uint256));
                require(router == ROUTER, "SV: invalid spender");
            }
        } else {
            revert("Invalid op funtion signature");
        }

        return true;
    }

    function validateSellTx(
        bytes memory data,
        address sender,
        address token,
        address WRAPPED_NATIVE_TOKEN,
        uint256 spentAmount
    ) internal pure {
        bytes4 fncSig = bytes4(data.slice(0, 4));

        bytes memory funcData = data.slice(4, data.length - 4);

        if (fncSig == EXACT_OUTPUT_SINGLE_SELECTOR) {
            ExactOutputSingleParams memory params = abi.decode(
                funcData,
                (ExactOutputSingleParams)
            );

            require(
                params.tokenOut == WRAPPED_NATIVE_TOKEN,
                "SV: only accept Wrapped native token as tokenOut"
            );
            require(params.tokenIn == token, "SV: Wrong token out");
            require(
                params.amountOut == spentAmount,
                "SV: spent amounts mismatch"
            );
            require(params.recipient == sender, "SV: Wrong recipient");
        } else if (fncSig == UNIV2_SWAP_TOKENS_FOR_EXACT_ETH) {
            (uint256 amountOut, , address[] memory path, address to, ) = abi
                .decode(
                    funcData,
                    (uint256, uint256, address[], address, uint256)
                );
            require(amountOut == spentAmount, "BV: invalid amount in");
            require(path[0] == token, "BV: invalid token in");
            require(
                path[path.length - 1] == WRAPPED_NATIVE_TOKEN,
                "BV: invalid token out"
            );
            require(to == sender, "BV: invalid recipient");
        } else {
            revert("SV: swap function is not supported");
        }
    }
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import {BaseAuthorizationModule} from "../BaseAuthorizationModule.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@account-abstraction/contracts/core/Helpers.sol";
import {ISessionKeyManager} from "../../interfaces/ISessionKeyManager.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "./BuyValidator.sol";
import "./SellValidator.sol";
import "./PreApproveValidator.sol";

interface ISmartAccount {
    function getOwner(address smartAccount) external view returns (address);
}

contract SwapSessionKeyManager is BaseAuthorizationModule {
    address public ECDSA_MODULE_ADDRESS;
    address public WRAPPED_NATIVE_TOKEN;
    address public FEE_RECIPIENT;
    uint48 public constant FEE_RATE_PRECISION = 1e6;
    uint48 public constant HALF_FEE_RATE_PRECISION = 5e5;
    uint48 public FEE_RATE;
    uint256 public FEE_THRESHOLD;

    constructor(
        address _ECDSA_MODULE_ADDRESS,
        address _WRAPPED_NATIVE_TOKEN,
        address _FEE_RECIPIENT,
        uint48 _FEE_RATE,
        uint256 _FEE_THRESHOLD
    ) {
        ECDSA_MODULE_ADDRESS = _ECDSA_MODULE_ADDRESS;
        WRAPPED_NATIVE_TOKEN = _WRAPPED_NATIVE_TOKEN;
        FEE_RECIPIENT = _FEE_RECIPIENT;
        require(
            _FEE_RATE < FEE_RATE_PRECISION,
            "Fee rate must be less than 100%"
        );
        FEE_RATE = _FEE_RATE;
        FEE_THRESHOLD = _FEE_THRESHOLD;
    }

    /**
     * @dev mapping of owner to a session root
     */
    mapping(address => bytes32) public merkleRoot;

    /**
     * @dev mapping of owner to trading quota
     */
    mapping(address => uint256) public tradingQuota;

    /**
     * @dev mapping of session key to accumulated fee
     */
    mapping(address => uint256) public accumulatedFee;

    function setMerkleRootAndTradingQuota(bytes32 _merkleRoot, uint256 _tradingQuota) external {
        merkleRoot[msg.sender] = _merkleRoot;
        tradingQuota[msg.sender] = _tradingQuota;
    }

    /**
     * @dev validates userOperation
     * @param userOp User Operation to be validated.
     * @param userOpHash Hash of the User Operation to be validated.
     * @return sigValidationResult 0 if signature is valid, SIG_VALIDATION_FAILED otherwise.
     */
    function validateUserOp(
        UserOperation calldata userOp,
        bytes32 userOpHash
    ) external virtual returns (uint256) {
        address sender = userOp.sender;
        address owner = ISmartAccount(ECDSA_MODULE_ADDRESS).getOwner(sender);

        (bytes memory moduleSignature, ) = abi.decode(
            userOp.signature,
            (bytes, address)
        );
        (
            uint48 validUntil,
            uint48 validAfter,
            address router,
            address token,
            address sessionKey,
            uint256 maxETHSpend,
            uint256 spentAmount,
            bool approveAll,
            bool isBuyOrder,
            bytes32[] memory merkleProof,
            bytes memory sessionKeySignature
        ) = abi.decode(
                moduleSignature,
                (
                    uint48,
                    uint48,
                    address,
                    address,
                    address,
                    uint256,
                    uint256,
                    bool,
                    bool,
                    bytes32[],
                    bytes
                )
            );

        bytes32 root = merkleRoot[owner];

        bytes32 leaf;
        if (!approveAll)
            leaf = keccak256(
                abi.encodePacked(
                    validUntil,
                    validAfter,
                    token,
                    sessionKey,
                    sender,
                    router,
                    maxETHSpend
                )
            );
        else
            leaf = keccak256(
                abi.encodePacked(
                    validUntil,
                    validAfter,
                    sessionKey,
                    sender,
                    router,
                    maxETHSpend
                )
            );
        if (!MerkleProof.verify(merkleProof, root, leaf)) {
            revert("SessionNotApproved");
        }

        bool validSig = ECDSA.recover(
            ECDSA.toEthSignedMessageHash(userOpHash),
            sessionKeySignature
        ) == sessionKey;

        bool validOp;

        if (spentAmount > 0) {
            require(
                token != address(0) && token != WRAPPED_NATIVE_TOKEN,
                "Specified token must be non-native"
            );

            if (spentAmount > maxETHSpend)
                revert("Maximum ETH per tx exceeded");
            uint256 currentTradingQuota = tradingQuota[owner];
            if (spentAmount > currentTradingQuota)
                revert("Trading quota exceeded");
            tradingQuota[owner] = currentTradingQuota - spentAmount;

            uint256 fee = (spentAmount * FEE_RATE + HALF_FEE_RATE_PRECISION) /
                FEE_RATE_PRECISION;

            uint256 currentAccumulatedFee = accumulatedFee[sessionKey] + fee;
            uint256 payment;
            if (currentAccumulatedFee >= FEE_THRESHOLD) {
                payment = currentAccumulatedFee;
                accumulatedFee[sessionKey] = 0;
            } else {
                payment = 0;
                accumulatedFee[sessionKey] = currentAccumulatedFee;
            }

            if (isBuyOrder) {
                validOp = BuyValidator.validateBuy(
                    userOp,
                    token,
                    spentAmount,
                    payment,
                    WRAPPED_NATIVE_TOKEN,
                    router,
                    FEE_RECIPIENT
                );
            } else {
                validOp = SellValidator.validateSell(
                    userOp,
                    token,
                    spentAmount,
                    payment,
                    WRAPPED_NATIVE_TOKEN,
                    router,
                    FEE_RECIPIENT
                );
            }
        } else {
            // approve
            validOp = PreApproveValidator.validatePreApprove(
                userOp,
                token,
                router
            );
        }

        return
            _packValidationData(
                //_packValidationData expects true if sig validation has failed, false otherwise
                !(validOp && validSig),
                validUntil,
                validAfter
            );
    }

    /**
     * @dev returns the SessionStorage object for a given owner
     * @param owner owner address
     */
    function getSessionRoot(address owner) external view returns (bytes32) {
        return merkleRoot[owner];
    }

    /**
     * @dev returns the accumulated fee of a given sessionKey
     * @param sessionKey sessionKey address
     */
    function getAccumulatedFee(
        address sessionKey
    ) external view returns (uint256) {
        return accumulatedFee[sessionKey];
    }

    /**
     * @dev isValidSignature according to BaseAuthorizationModule
     * @param _dataHash Hash of the data to be validated.
     * @param _signature Signature over the the _dataHash.
     * @return always returns 0xffffffff as signing messages is not supported by SessionKeys
     */
    function isValidSignature(
        bytes32 _dataHash,
        bytes memory _signature
    ) public pure override returns (bytes4) {
        (_dataHash, _signature);
        return 0xffffffff; // do not support it here
    }
}

